import React, { Component } from 'react';
import Logo from '../logo.png';
import '../css/layout.css'
class Hero extends Component {
  state = {
        name: "Diana Beatrice",
        description:"Have something great in mind? Feel free to contact me.",
        description2:"I'll help you to make it happen."
    };
    render() {    
       
        return(
            <header id="home">
            <a href="/"><img className="logo" src={Logo} alt="Logo" /></a>
            <div className="row banner">
               <div className="banner-text">
                  <h2 className="responsive-headline">Hello! I'm {this.state.name}.</h2>
                  <h3 className="responsive-headline">Consult, Design, and Develop Websites</h3>
                  <h4>{this.state.description}<br/>{this.state.description2}<br/></h4>
                  <hr />
                  <div >
                  <div className="hero__btn">
                  <button className="btn">LET'S MAKE CONTACT</button>
                </div>
                </div>
               </div>
            </div>
         </header>
          );
        }
      }
      
      export default Hero;
